INSERT INTO smartchemical.dbo.Company(description, companyName, companyAddress, companyFullName, companySlogan, companyIcon, longDescription, companyAlias, latitude, longitude, companyUrl, companyCityId, companyType) VALUES (N'河南佰利联化学股份有限公司', N'佰利联化学', N'河南焦作市中站区', N'河南佰利联化学股份有限公司', N'诚信，勤奋，创新，共赢', N'./img/company_icons/baililian.png', N'<h3>公司介绍</h3>
		<p>河南佰利联化学股份有限公司是一家专注于钛、锆精细粉体材料研发制造的大型无机精细化工企业，总资产超过30亿元。深交所上市公司，股票代码：002601。</p>
		<p>公司是国家级高新技术企业、全国化工500强、河南省双百（百强、百高）企业、河南省优秀民营企业。主营钛白粉、锆制品、硫酸等三大系列产品，生产规模均处全国前列。产品畅销全国，并出口美国、日本、巴西等几十个国家和地区，多次荣获 “河南省重点出口、进口企业”称号。公司通过ISO9001:2008质量管理体系认证、GB/T24001―2004环境体系认证和GB/T28001―2001职业健康安全管理体系认证。</p>
		<p>公司下设科研技术中心、河南省纳米材料工程技术中心产业化基地、河南省钛锆粉体工程技术中心、博士后工作站等研究机构，拥有多项具有独立自主产权的金红石型钛白粉关键技术，具备10万吨/年单套硫酸法钛白生产线的设计、安装能力，是我国塑料异型材、化妆品、非颜料型钛白粉、氯氧化锆、二氧化锆、硫酸锆、碳酸锆等锆制品和硫酸铝国家和行业标准的主要起草单位。</p>
		<p>环保是佰利联生存与发展之本，发展绿色循环经济，实现清洁生产是公司实现跨越式发展的根基和方向。公司在行业内率先采用硫铁钛联合法技术，较好地解决了钛白生产中资源能源的合理、综合利用，显著降低了消耗和成本，得到环保部和工信部的认可。公司以“减量化、再循环、再利用”为原则，从源头、过程和结果三个方面，建立系统化的节能减排和资源综合利用体系，努力实现废物零排放，走上了一条可持续发展的和谐之路。</p>
		<p>十二五期间，公司将依托钛、锆产业和完善的水、电、蒸汽、燃气、水处理等配套设施，建设佰利联工业园区承载产业项目发展。园区分为精细化工产业园和新材料产业园两部分，其中精细化工产业园以钛白粉原料型产品为主体，新材料产业园以钛白粉延伸产品、终端产品和精细纳米、陶瓷新材料为主体。争取到十二五末，使佰利联工业园区钛白粉规模达到30万吨。</p>
		', N'佰利联化学', 113.137239, 35.246126, N'http://sales66722.company.lookchem.cn/', 237, 1);
INSERT INTO smartchemical.dbo.Company(description, companyName, companyAddress, companyFullName, companySlogan, companyIcon, longDescription, companyAlias, latitude, longitude, companyUrl, companyCityId, companyType) VALUES (N'武汉方圆钛白有限公司', N'方圆钛白', N'湖北省武汉市~口区汇丰企业总部', N'武汉方圆钛白有限公司', N'科技先导，满足要求，持续改进，追求卓越', N'./img/company_icons/fangyuantaibai.png', N'<h3>公司介绍</h3>
		<p>“方圆钛白”系国内钛白行业核心企业之一。自2000年通过改制和重组以来，“方圆钛白”坚持绿色环保和精品名牌战略，实现了跨越式发展。企业先后获评“全国民营化工优秀企业”、“中国化工卓越品牌”、“湖北名牌产品”、“湖北省著名商标”等荣誉；通过了国家高新技术企业认定管理《新办法》的认定和质量/环境/职业健康安全（ISO9001：2000、ISO14001：2004、GB/T28001-2001）“三合一”管理体系认证。</p>
		<p>企业始终坚持“持续改进、追求卓越”的质量管理方针，不断实现产品及服务质量的提升和产品结构的升级。在已有年产1万吨高档锐钛型钛白粉的基础上，又陆续建成占地约15万平方米，年产量6万吨（一期2万吨，二期4万吨）的金红石型钛白粉生产线，新生产线主要工序均采用计算机控制，全面应用国内最前沿的钛白粉制造工艺，部分借鉴和引进了国外先进技术和设备。成熟的生产工艺、严格管理的生产流程使“方圆”牌钛白粉除具有外观白度好、消色力高、吸油量低、粒径及粒度分布均匀、分散性和遮盖力高等特点外，在产品批次、应用性能的稳定性以及色调、色泽要求特殊性等方面更是独具特色。</p>
		<p>在生产经营过程中，企业矢志秉持“绿色”发展理念，继在国内同行业中首家通过ISO14001国际环境管理体系认证之后，又率先攻克了“废酸浓缩工业化生产”等制约行业发展的环境难题；《钛白粉“绿色”生产》项目已纳入国家火炬计划。环境保护、清洁生产和循环经济水平居行业前列，走出了一条自主创新、与时俱进的可持续发展之路，形成了独具特色的质量和环境品牌。</p>
		<p>企业将取国内外行业之长、创方圆钛白之新，以绿色产品和优质服务回报社会！以做国内钛白粉卓越供应商和建设“方圆”国际品牌作为一贯追求，不断谋求更健康的发展！</p>
		', N'方圆钛白', 113.137239, 35.246126, N'http://www.fytb.com/_d271227310.htm', 250, 1);
INSERT INTO smartchemical.dbo.Company(description, companyName, companyAddress, companyFullName, companySlogan, companyIcon, longDescription, companyAlias, latitude, longitude, companyUrl, companyCityId, companyType) VALUES (N'江西添光钛业有限公司', N'添光钛业', N'抚州市临川区抚北镇安塘路4号', N'江西添光钛业有限公司', N'诚信，互利', N'./img/company_icons/jiangxitianguang.png', N'<h3>公司介绍</h3>
