INSERT INTO smartchemical.dbo.Company(description, companyName, companyAddress, companyFullName, companySlogan, companyIcon, longDescription, companyAlias, latitude, longitude, companyUrl, companyCityId, companyType) VALUES (N'济南裕兴化工有限责任公司', N'蓝星裕兴', N'济南市天桥区济南新材料产业园区裕兴路100号', N'济南裕兴化工有限责任公司', N'打造中国钛白样板式企业，成为全球行业知名者。', N'./img/company_icons/nanxingyuxing.png', N'<h3>公司介绍</h3>
		<p>济南裕兴化工有限责任公司隶属于中国蓝星（集团）股份有限公司，是中国化工集团公司下属的三级公司。公司始建于1919年，距今已有九十多年的历史，为中国民族化工的发展做出了不可磨灭的贡献。</p>
		<p>公司主导产品“生生”牌铬盐、钛白粉系列产品通过了ISO9001质量体系认证，市场占有率达10%和20%，是国内生产最早、规模最大的公司之一，产品质量和工艺技术达到国内领先水平，多次荣获国家产品质量最高荣誉。</p>
		<p>公司新厂区于2010年6月建成，占地1170余亩，现有员工1600余人，拥有10万吨/年金红石钛白粉生产装置、30万吨/年硫磺制酸装置、3×75吨/时中温中压锅炉供热装置、水处理、废酸浓缩、110千伏变电站、工业水站、循环水站、铁路专用线等26项子装置。</p>
		<p>公司拥有销售、服务和信息反馈垂直一体的健全的营销网络，在上海、天津、广州、沈阳设有办事处，产品远销东南亚、欧美、非洲等20多个国家和地区，具有较高的知名度和美誉度，已成为中国铬、钛行业知名品牌。</p>
		<p>公司积极履行央企社会责任，始终将环境保护置于一切工作之首，实施HSE管理体系，采用ISO9001:2000质量管理体系，发展循环经济，采用先进工艺和设备处理产品生产中的废弃物，达到了“零排放”，环境保护水平走在全国同行业前列，实现了“经济效益、社会效益、环境效益”的多元共赢。</p>
		<p>公司搬迁改造建设10万吨/年钛白粉项目已在济南化工产业园建设完成，二期工程将于“十二五”期间建成。一个国内规模最大、装备和技术水平最先进、质量最好、世界级制造、节能减排、环境友好型样板式企业将在这片热土上描绘更加绚丽的画卷。</p>
		<p>中国化工大道远行，砥砺图强；济南裕兴生生不息，永驻辉煌！</p>
		', N'蓝星裕兴', 113.137239, 35.246126, N'http://www.yuxing-chem.com/jnyx/index.htm', 65, 1);
INSERT INTO smartchemical.dbo.Company(description, companyName, companyAddress, companyFullName, companySlogan, companyIcon, longDescription, companyAlias, latitude, longitude, companyUrl, companyCityId, companyType) VALUES (N'中核华原钛白股份有限公司', N'中核钛白', N'上海市浦东新区祖冲之路', N'中核华原钛白股份有限公司', N'中核钛白，杰出的化工企业。', N'./img/company_icons/zhonghetaibai.png', N'<h3>公司介绍</h3>
		<p>中核华原钛白股份有限公司是中国首家引进国外先进技术和关键设备的第一座万吨级高档钛白粉生产企业，生产过程应用德国SIEMENS集散控制系统自动控制，其R-215等高档金红石型钛白粉产品广泛应用于涂料、塑料、造纸、油墨等领域。产品覆盖国内并远销世界各地。 公司发挥军工企业人才、技术和科研优势，注重高科技含量产品的开发研制，不断扩展生产规模，年生产能力达5万吨。经过十几年的建设，目前发展成为集生产、科研、开发、应用于一体的大型现代高科技股份制上市企业。“泰奥华”牌钛白粉在海内外享有盛誉，被评为“甘肃省著名商标”。企业被授予“甘肃工业六十强企业”、“双十企业”和“守合同重信用企业”。 公司追求“以质量求生存，以创新促发展”的经营理念，有全面的质量保证体系，现代化的技术检测手段和良好的售后服务网，于2002年通过了ISO9001:2000版质量认证。“奥泰华”钛白粉产品经法国科技质量监督评价委员会和中国国际锚机促进委员会联合审定为“高质量科技产品及向欧盟市场特别推荐产品。” 公司坚持“发展钛业，做强主业，多种经营，壮大实力”的发展战略，致力于现代企业制度建设，不断进行体制创新、机制创新和技术创新，建立适应市场变化经营机制和管理体制，努力把公司建成一个资本多元化、产业多元化、企业现代化的国内名牌和国际知名企业。</p>
		', N'中核钛白', 113.137239, 35.246126, N'http://www.sinotio2.com/', 458, 1);
INSERT INTO smartchemical.dbo.Company(description, companyName, companyAddress, companyFullName, companySlogan, companyIcon, longDescription, companyAlias, latitude, longitude, companyUrl, companyCityId, companyType) VALUES (N'攀枝花大互通钛业有限公司', N'攀枝花大互通', N'青岛李沧区金水路', N'攀枝花大互通钛业有限公司', N'以市场为导向，以技术为核心竞争力', N'./img/company_icons/dahutong.jpg', N'<h3>公司介绍</h3><p>攀枝花大互通钛业有限公司坚持以市场为导向，以技术为核心竞争力，不断扩大生产规模及研发新产品、新工艺以增强企业发展后劲。公司先后从国外引进先进的钛白粉生产装备及水解水洗技术。2000年，公司在富民县投资2亿元人民币，建成西南地区的集科研、开发于一体的年产3万吨锐钛型钛白粉厂。2003年，公司成功在全国开发出“兰相”化纤及水溶性涂料专用钛白粉，广泛应用于化纤、涂料、高档色母粒、造纸、塑料、油墨、油漆等领域，深得用户好评。2005年，公司投资750万元成立“钛白工程技术研究中心”，专司研究开发钛白粉生产新技术和新产品。公司致力于走出云南，充分利用自身的技术、市场等优势，嫁接外地资源优势发展自己。2006年，公司在四川攀枝花钒钛高科技园投资5亿元人民币，建设年产6万吨高档级钛白粉及配套年产15万吨硫磺制酸和8万吨钛渣项目。预计该项目于2007年9月投料生产。在未来两年的发展中，公司将在产量、质量上实现新的飞跃。预计2008年将实现年产高档钛白粉7万吨，同时实现年产高钛渣8万吨，年产硫酸15万吨，实现产值13亿元人民币。2009年将实现年产高档钛白粉9万吨，高钛渣10万吨，硫酸20万吨，实现年产值19亿元人民币。</p>', N'大互通', 0.0, 0.0, N'http://yndhtty.cn.china.cn/', 70, 1);
INSERT INTO smartchemical.dbo.Company(description, companyName, companyAddress, companyFullName, companySlogan, companyIcon, longDescription, companyAlias, latitude, longitude, companyUrl, companyCityId, companyType) VALUES (N'攀枝花海峰鑫化工有限公司', N'攀枝花海峰鑫', N'攀枝花钒钛产业园区', N'攀枝花海峰鑫化工有限公司', N'攀枝花海峰鑫化工，杰出的化工公司', N'./img/company_icons/haifengxin.jpg', N'<h3>公司介绍</h3><p>攀枝花市海峰鑫化工有限公司成立于2006年11月，属于民营股份制公司，现有从业人员500人。公司位于攀枝花钒钛产业园区，占地160余 亩，注册资金7000万。</p>
		   <p>公司有6万吨/年金红石型钛白粉生产装置，2012年3月建成投产，有当前国内最大的钛白单线产能，实现对废酸浓缩处理，对包括转窑、硫酸装置的余热进行回收利用，不仅环保节能达标，投入产出比亦居全国第一。配套有20万吨/年硫磺制酸生产装置。 主营牌号为R-618、R-9925、R9930等金红石型钛白粉产品，广泛用于涂料、油墨、造纸、塑料等领域。</p>
		   <p>公司以“成为同行业界的优秀典范”为目标，组建了高素质的管理团队，自主设计的6万吨/年金红石型钛白粉的生产线充分吸取了国内钛白装置的先进技术产经验并进行了多项自主创 新和工艺优化，使生产成本和能源消耗方面有较大的竞争优势。</p>
		   <p>该公司坚守了“责任 稳定 特色 环保”的管理理念，投资安装了废酸浓缩装置，建立了污水处理站，实现了废水、废气的达标排放。</p>', N'海峰鑫', 0.0, 0.0, N'http://www.pzhhfx.com/', 349, 1);
INSERT INTO smartchemical.dbo.Company(description, companyName, companyAddress, companyFullName, companySlogan, companyIcon, longDescription, companyAlias, latitude, longitude, companyUrl, companyCityId, companyType) VALUES (N'南京钛白化工有限责任公司', N'金浦/南京钛白', N'南京化学工业园区大纬东路229号', N'南京钛白化工有限责任公司', N'理想成就未来、追求永无止境', N'./img/company_icons/jinputaiye.jpg', N'<h3>公司介绍</h3>
		   <p>南京钛白化工有限责任公司成立于1957年。2005年经资产重组成为民营企业。公司坐落于南京化学工业园区，占地面积12万平方米，现有在岗员工500余人，其中技术人员占比35%以上，从事研究与试验开发人员近70人。2008年，公司“5万吨/年硫酸法钛白粉环保搬迁改造项目”在南京化学工业园建成并投产，2012年，公司实施了“钛白粉质量升级及扩能技术改造项目”，产能规模达10万吨/年。南京钛白公司工艺技术及生产装备先进，产品品质优良，环保治理完善，废气、废酸、废水及废渣等均实现全面治理和综合利用，体现循环经济，实现清洁生产。</p>
		   <p>南京钛白公司是国内最大的硫酸法钛白粉生产企业之一，2012年8月通过 “高新技术企业” 认定，已通过ISO9001：2000质量管理体系认证。公司产品广泛应用于涂料、造纸、化纤、油墨、塑料管型材、薄膜、橡胶、皮革、化妆品等领域，畅销国内市场，并出口到东南亚、非洲、美洲等国家或地区，享誉国内外，受到国内外用户的一致好评。</p>
		   <p>南京钛白全资子公司徐州钛白的“8万吨钛白粉（硫、钛一体化热能利用）搬迁一期工程项目”正按计划推进，规划年产钛白粉8万吨，并配套建设30万吨/年硫酸生产线，该项目预计2014年建成投产。</p>
		   <p>南京钛白公司将继续发挥区位优势、生产技术优势、生产成本优势、研发和人才优势、精细化管理优势和产品质量优势，持续做好环境保护工作，深化资源综合利用水平，提升资源利用率和综合经济效益，不断提高市场占有率和行业影响力，与此同时，公司还将根据市场情况审慎决策，充分利用资本市场，通过并购重组等方式扩大公司的业务规模，提升企业综合实力，快步进入国内钛白行业第一方阵，为国内钛白产业及地方经济发展再作新贡献。</p>', N'金浦钛白', 0.0, 0.0, N'http://www.nthcl.com/', 34, 1);
INSERT INTO smartchemical.dbo.Company(description, companyName, companyAddress, companyFullName, companySlogan, companyIcon, longDescription, companyAlias, latitude, longitude, companyUrl, companyCityId, companyType) VALUES (N'宁波新福钛白粉有限公司', N'宁波新福', N'浙江宁波化学工业区跃进塘路1号', N'宁波新福钛白粉有限公司', N'诚信，创新，品质，服务', N'./img/company_icons/ningboxinfu.jpg', N'<h3>公司介绍</h3>
		   <p>宁波新福钛白粉有限公司是一家生产各类高档金红石型和高纯度非颜料级钛白粉的国家级高新技术企业。拥有一家年产60万吨硫酸的子公司。 公司地理位置优越,紧邻杭州湾跨海大桥。距东方国际大港 ― 北仑港 20KM ，离宁波机场 20 公里 ，距上海市约200 公里 。公司工艺设备先进，技术力量雄厚，注重环境保护， 拥有一支多年从事钛白粉生产的专业技术队伍，有省级工程技术中心及与中科院宁波材料所共同组建了“钛白粉新材料工程技术研究中心”。公司已通过 CQC 认证的 ISO9001：2008国际质量管理体系、ISO14001:2004环境管理体系、GB/T20881-2001职业健康安全管理体系。</p>
		   <p>公司的金红石型NTR-606产品，质量稳定，颜色蓝白、遮盖力强、分散性优异，广泛应用于涂料、塑料、造纸、油墨等领域，可替代进口产品使用；高纯度非颜料级钛白粉产品，生产历史悠久，性能优良、稳定，品质卓越。在陶瓷、冶金、玻璃、电子等多个行业得到了广泛应用。公司产品远销美国、欧洲、中东、日韩、南美等国家和地区。公司还可以按照用户的要求，研制生产各种特殊规格钛白粉。 我们将立足市场需要，继续向钛白粉的新领域拓展，不断提供卓越的产品和优质的服务！</p>
		   ', N'宁波新福', 0.0, 0.0, N'http://www.xinfu-tio2.com/', 2, 1);
INSERT INTO smartchemical.dbo.Company(description, companyName, companyAddress, companyFullName, companySlogan, companyIcon, longDescription, companyAlias, latitude, longitude, companyUrl, companyCityId, companyType) VALUES (N'山东东佳集团', N'山东东佳', N'淄博市博山区秋谷横里河55号', N'山东东佳集团', N'山东东佳集团，杰出，诚信', N'./img/company_icons/shandongdongjia.gif', N'<h3>公司介绍</h3>
		   <p>山东东佳集团成立于2002年，下设全资、控股公司4家，占地面积70万平方米，员工2200余人，其中科技人员近600人。集团年产氧化钛16万吨，硫酸90万吨，硫酸铵6万吨，已发展成为亚洲特大氧化钛生产基地之一。</p>
		   <p>科技创新是企业长远发展的不竭动力。多年来，我们始终秉承科技兴企、创新发展的理念，每年在科技研发方面的平均投入超过销售收入的3.7%，专业技术人员近600人，其中，高级技术人员50余人。在现有的博士后科研工作站、院士工作站、省级企业技术中心和泰山学者岗位的基础上，建立起国内氧化钛行业领先的技术研发中心。与中国科学院过程工程研究所合作完成“钛白清洁生产技术与示范”项目；通过了由国家工信委、中国环境科学院、中国石油和化学工业联合会组织的“硫铵钛联产法钛白粉清洁生产工艺”科技成果鉴定，该工艺将作为国内钛白行业的清洁生产标准在全国范围内推广。企业创新能力的不断提升，在有效稳定产品质量的同时，极大地提高了企业品牌知名度和市场占有率，也使得企业在激烈的市场竞争中逐步掌握了行业话语权。</p>
		   <p>集团坚持科学发展，严格按照“资源－产品－再生资源”的模式延伸产业链，逐步形成了“以氧化钛为龙头，配套硫酸、蒸汽回用、副产硫铵、余热发电、综合利用”的循环产业链。国家发改委下发的《钒钛资源综合利用和产业发展“十二五”规划》作为我国钒钛资源高效配置、产业布局调整、技术升级改造的重要指南，其中明确将东佳集团列入钒钛资源产业基地，这在钛白行业尚属首例，也是对东佳近年来在钛资源综合利用方面所取得成果的高度肯定。集团先后荣获“全国综合利用先进单位”、全市“发展循环经济先进集体”、“淄博市最具社会责任感优秀企业”等称号。东佳是全国化工环境保护先进单位，荣获“低碳山东”先进企业称号，被省政府授予山东省节能先进单位，中国石油和化学工业联合会授予本公司“石油和化工行业环境保护与清洁生产重点支撑技术单位”。</p>', N'山东东佳', 0.0, 0.0, N'http://www.dongjiagroup.com/', 76, 1);
INSERT INTO smartchemical.dbo.Company(description, companyName, companyAddress, companyFullName, companySlogan, companyIcon, longDescription, companyAlias, latitude, longitude, companyUrl, companyCityId, companyType) VALUES (N'攀枝花钛海科技（集团）有限公司', N'钛海科技', N'攀枝花市钒钛产业园区', N'攀枝花钛海科技有限公司', N'自信源于品质，品质源于实践', N'./img/company_icons/taihaikeji.jpg', N'<h3>公司介绍</h3>
		   <p>攀枝花钛海科技（集团）有限公司由攀枝花市钛海科技有限责任公司、攀枝花鼎星钛业有限公司、攀枝花市金港钛业有限公司、攀枝花民睿鑫商贸有限公司，建水县云川实业有限公司组建。</p>
		   <p>集团公司现已形成集矿山采选、钛矿冶炼、钛白粉生产为一体的完整钛产业链，产品覆盖钛精矿、酸溶性高钛渣和钛白粉。</p>
		   <p>集团公司现年产：</p>
		   <p>● 金红石型钛白8万吨</p>
		   <p>● 催化剂/锐钛型钛白粉4万吨</p>
		   <p>● 钛精矿10万吨</p>
		   <p>● 酸溶性钛渣5万吨</p>
		   <p>2013年钛海集团的钛白粉产量达到7万吨，位居攀枝花市众多钛白粉企业中第一位</p>', N'钛海科技', 0.0, 0.0, N'http://www.taihaichina.cn/', 349, 1);
INSERT INTO smartchemical.dbo.Company(description, companyName, companyAddress, companyFullName, companySlogan, companyIcon, longDescription, companyAlias, latitude, longitude, companyUrl, companyCityId, companyType) VALUES (N'云南泽昌钛业有限公司', N'泽昌钛业', N'云南省昆明市盘龙区白塔路延长线七彩俊园一期4幢14楼', N'云南泽昌钛业有限公司', N'泽昌钛业，杰出的化工公司', N'./img/company_icons/zechangtaiye.jpg', N'<h3>公司介绍</h3>
		   <p>云南泽昌钛业有限公司于2008年正式成立，公司注册资金5180万元，经过2年的工厂建设和设备调式，于2010年正式投产。泽昌钛业选址风景秀丽、气候宜人的云南省昆明市富民县境内，在当地拥有一个储量40万吨的钛矿厂，是一家集原料、科研、生产、销售、服务为一体的现代化企业。泽昌钛业生产基地建于昆明市钛产业工业园区内，一期占地面积300余亩，总投资近4亿元人民币，年生产能力5万吨金红石型钛白粉。目前泽昌钛业已通过云南省“昆明市企业技术中心”认定，标志着企业的技术和研发能力又跨上了一个新台阶。</p>
		   ', N'泽昌钛业', 0.0, 0.0, N'http://www.ynzcty.com/', 366, 1);
INSERT INTO smartchemical.dbo.Company(description, companyName, companyAddress, companyFullName, companySlogan, companyIcon, longDescription, companyAlias, latitude, longitude, companyUrl, companyCityId, companyType) VALUES (N'四川龙蟒集团有限公司', N'龙蟒集团', N'成都市高新区高朋大道23号', N'四川龙蟒集团有限公司', N'', N'./img/company_icons/longmang.png', N'<h3>公司介绍</h3>
		   <p>龙蟒集团是磷化工、钛化工、生物化工和钒钛磁铁矿综合开发利用为一体的大型民营企业集团。现已形成德阳、襄阳两大硫-磷-钛化工产业基地、眉山高新生物化工产业基地和攀枝花钒钛磁铁矿综合开发利用基地。集团公司现有员工近10000人，其中各类专业技术人员2000余人。2014年产品销售收入80多亿元，上交税金7亿多元，出口创汇3亿美元。</p>
		   ', N'龙蟒集团', 0.0, 0.0, N'http://www.lomon.com', 342, 1);
